// efficient maintenance of shortest distances

var Set = require('..');

var set = new Set({
  hash: function(item) {
    return item.id;
  },
  compare: function(a, b) {
    return a.distance - b.distance;
  }
});

set.add({
  id: '00343e786c3fd689cc9949a9c8837a72',
  distance: 14
});

set.add({
  id: '1e2d53b179139ca55149da27e31cd6c3',
  distance: 16
});

set.add({
  id: '98f2611ce14807ad364cd882291e6e4d',
  distance: 22
});

set.add({
  id: '175ec6084a7838a29b8a30017a24ba00',
  distance: 12
});

set.add({
  id: 'e3f89c5e3f3caec494d8d1269d824f89',
  distance: 18
});

// closest two nodes
var nodes = set.slice(0, 2);

console.log(nodes);

